function animacaoCarrinho(){$(".navbar-icon-cart").click(function(a){$(".carrinho").animate({right:"0px"},"fast",function(){$(".backNav").fadeIn(500)})}),$(".carrinho > h4, .backNav").click(function(a){$(".carrinho").animate({right:"-80%"},"fast",function(){$(".backNav").fadeOut(200)})})}function startLoader(){var a=$("<div class='loader'></div>").html("<div class='loaderContent'></div>");$("body").append(a).fadeIn(900)}function stopLoader(){window.setTimeout(function(){$(".loader").fadeOut("slow")},1e3)}$(document).ready(function(){startLoader(),stopLoader(),animacaoCarrinho()});