function listarProdutos(){$.ajax({type:"GET",url:"db/products.json",dataType:"json",contentType:"application/json",success:function(o){var n=JSON.stringify(o);n=JSON.parse(n);for(var r=n.products.length,t=0;t<r;t++){var p=$("#catalogo-produtos div").first().clone();p.find(".produto-imagem").attr("src",n.products[t].image),p.find("a.produto-nome").append(n.products[t].name),p.find(".produto-preco").append(n.products[t].regular_price),p.find(".produto-desconto").append(n.products[t].discount_percentage+"<span> de desconto.</span>"),p.find(".produto-preco-desconto").append(n.products[t].actual_price),p.find(".produto-parcelas").append("<span>Parcelas de </span>"+n.products[t].installments);for(var a=0;a<n.products[t].sizes.length;a++)p.find(".btn-tam").append("<span>"+n.products[t].sizes[a].size+"</span>");$("#catalogo-produtos").append(p),p.fadeIn()}},error:function(o){console.log("Error "+JSON.stringify(o))}})}$(document).ready(function(){listarProdutos()});